ESX=nil;TriggerEvent('esx:getSharedObject',function(a)ESX=a end)ESX.RegisterServerCallback('Identify:GetPlayerIdentity',function(b,c,d)local e=b;local f=ESX.GetPlayerFromId(d)local g,h=string.match(f.name,"([^_]+)_([^_]+)")local i=f.job.name;local j=[[]]TriggerEvent('esx_license:getLicenses',d,function(k)local l={}for m=1,#k,1 do l[k[m].type]=true end;if l['dmv']then j=j.."DMV,\n"end;if l['drive']then j=j.."Drive,\n"end;if l['drive_bike']then j=j.."Bike,\n"end;if l['drive_truck']then j=j.."Truck,\n"end;if l['weapon']then j=j.."Weapon,\n"end end)Wait(100)c({["firstname"]=g,["lastname"]=h,["job"]=i,["division"]=j})end)RegisterCommand('sl',function(b)local n=10;local o=GetPlayerPed(b)local p=GetEntityCoords(o)local q=ESX.GetPlayers()for m=1,#q,1 do local r=q[m]local s=GetPlayerPed(r)local t=GetEntityCoords(s)local u=#(p-t)if u<=n then TriggerClientEvent('Identify:ShowIdentify',r,b)end end end)
